package com.mzth.createcause.adapter;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentPagerAdapter;import java.util.List;/** * Created by CreateCause on 2017/9/26 0026. */public class MainAdapter extends FragmentPagerAdapter {    private List<Fragment> mFragments;    public MainAdapter(FragmentManager fm, List<Fragment> mFragments) {        super(fm);        this.mFragments = mFragments;    }    @Override    public Fragment getItem(int position) {        return mFragments.get(position);    }    @Override    public int getCount() {        return mFragments == null ? 0 : mFragments.size();    }}