package com.mzth.createcause.adapter.home;import android.content.Context;import android.graphics.Bitmap;import android.support.annotation.LayoutRes;import android.support.annotation.Nullable;import android.support.v7.widget.RecyclerView;import android.text.TextUtils;import android.view.LayoutInflater;import android.view.TextureView;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import com.bumptech.glide.Glide;import com.bumptech.glide.request.RequestOptions;import com.chad.library.adapter.base.BaseQuickAdapter;import com.chad.library.adapter.base.BaseViewHolder;import com.mzth.createcause.R;import com.mzth.createcause.entity.home.HomeEntity;import com.mzth.createcause.util.CommonUtil;import com.squareup.picasso.Picasso;import java.util.List;/** * Created by CreateCause on 2017/9/26 0026. */public class HomeAdapter extends BaseQuickAdapter<HomeEntity.HomeContents, BaseViewHolder> {    public HomeAdapter(@Nullable List<HomeEntity.HomeContents> data) {        super(R.layout.item_home, data);    }    @Override    protected void convert(BaseViewHolder helper, HomeEntity.HomeContents item) {        helper.setText(R.id.tv_item_home, item.getName());        ImageView imageView = helper.getView(R.id.iv_item_home);        String path = item.getImage();        Glide.with(mContext)                .load(path)                .apply(RequestOptions.centerInsideTransform()                        .error(R.drawable.icon_r_def)                        .placeholder(R.drawable.icon_r_def)                        .fallback(R.drawable.icon_r_def)                        .centerCrop()                )                .into(imageView);    }}