package com.mzth.createcause.adapter.news;import android.graphics.Bitmap;import android.support.annotation.Nullable;import android.text.TextUtils;import android.util.Log;import android.widget.ImageView;import com.bumptech.glide.Glide;import com.bumptech.glide.request.RequestOptions;import com.chad.library.adapter.base.BaseQuickAdapter;import com.chad.library.adapter.base.BaseViewHolder;import com.mzth.createcause.R;import com.mzth.createcause.entity.core.CoreContentEntity;import com.squareup.picasso.MemoryPolicy;import com.squareup.picasso.Picasso;import java.util.List;/** * Created by Administrator on 2017/10/9 0009. */public class NewsAdapter extends BaseQuickAdapter<CoreContentEntity.InfromationContentList, BaseViewHolder> {    public NewsAdapter(@Nullable List<CoreContentEntity.InfromationContentList> data) {        super(R.layout.item_news_one_pictrue, data);    }    @Override    protected void convert(BaseViewHolder helper, CoreContentEntity.InfromationContentList item) {        helper.setText(R.id.tv_news_content_1, item.getTitle());        helper.setText(R.id.tv_news_content_2, item.getCat_name() + " \u3000" + item.getComment() + "条评论 \u3000" + item.getAdd_time());        ImageView iv_news_content_2 = helper.getView(R.id.iv_news_content_2);        Glide.with(mContext)                .load(item.getImage())                .apply(RequestOptions.centerInsideTransform()                        .fallback(R.drawable.icon_default)                        .error(R.drawable.icon_default)                        .placeholder(R.drawable.icon_default)                        .centerCrop())                .into(iv_news_content_2);    }}