package com.mzth.createcause.adapter.share;import android.content.Context;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import com.mzth.createcause.R;/** * Created by CreateCause on 2017/9/28 0028. */public class SearchAdapter extends RecyclerView.Adapter<SearchAdapter.SearchViewHolder> {    private Context mContext;    private LayoutInflater mLayoutInflater;    private OnItemClickListener onItemClickListener;    public SearchAdapter(Context mContext) {        this.mContext = mContext;        this.mLayoutInflater = (LayoutInflater) mContext.getSystemService(Context.LAYOUT_INFLATER_SERVICE);    }    @Override    public SearchViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        return new SearchViewHolder(mLayoutInflater.inflate(R.layout.item_secrch, parent, false));    }    @Override    public void onBindViewHolder(SearchViewHolder holder, final int position) {        holder.contentView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                if (onItemClickListener != null) {                    onItemClickListener.onItemClick(position);                }            }        });    }    @Override    public int getItemCount() {        return 5;    }    class SearchViewHolder extends RecyclerView.ViewHolder {        private View contentView;        public SearchViewHolder(View itemView) {            super(itemView);            this.contentView = itemView;        }    }    public interface OnItemClickListener {        void onItemClick(int position);    }    public void setOnItemClickListener(OnItemClickListener onItemClickListener) {        this.onItemClickListener = onItemClickListener;    }}