package com.mzth.createcause.adapter.user;import android.util.Log;import android.widget.EditText;import android.widget.ImageView;import com.chad.library.adapter.base.BaseQuickAdapter;import com.chad.library.adapter.base.BaseViewHolder;import com.mzth.createcause.R;import com.mzth.createcause.entity.user.ExtractAccountEntity.AccountList;import java.util.ArrayList;import java.util.List;/** * Created by Administrator on 2017/10/11 0011. */public class ExtractAdapter extends BaseQuickAdapter<AccountList, BaseViewHolder> {    private List<ImageView> mImg;    public ExtractAdapter(List<AccountList> data) {        super(R.layout.item_extract, data);        mImg = new ArrayList<>();    }    @Override    protected void convert(BaseViewHolder helper, AccountList item) {        mImg.add((ImageView) helper.getView(R.id.tv_item_exterat));        helper.setText(R.id.tv_account_1, item.getBank_name());        helper.setText(R.id.tv_account_2, item.getZhifubao());        if (item.getType().equals("zhifubao")) {            helper.setImageResource(R.id.iv_account_1, R.drawable.pay);        } else if (item.getType().equals("weixin")) {            helper.setImageResource(R.id.iv_account_1, R.drawable.wechat);        } else {            helper.setImageResource(R.id.iv_account_1, R.drawable.icon_r_def);        }    }    public List<ImageView> getImgStatus() {        return mImg;    }}