package com.mzth.createcause.adapter.user;import android.graphics.Bitmap;import android.text.TextUtils;import android.widget.ImageView;import com.chad.library.adapter.base.BaseMultiItemQuickAdapter;import com.chad.library.adapter.base.BaseViewHolder;import com.mzth.createcause.R;import com.mzth.createcause.entity.FeedBackEntity;import com.squareup.picasso.Picasso;import java.io.File;import java.util.List;/** * Created by Administrator on 2017/10/20 0020. */public class FeedBackAdapter extends BaseMultiItemQuickAdapter<FeedBackEntity, BaseViewHolder> {    /**     * Same as QuickAdapter#QuickAdapter(Context,int) but with     * some initialization data.     *     * @param data A new list is created out of this one to avoid mutable list     */    public FeedBackAdapter(List<FeedBackEntity> data) {        super(data);        addItemType(FeedBackEntity.DEFAULT_IMG, R.layout.item_user_set_feed_back);        addItemType(FeedBackEntity.SELECT_IMG, R.layout.item_user_set_feed_back);    }    @Override    protected void convert(BaseViewHolder helper, FeedBackEntity item) {        ImageView mImageView = helper.getView(R.id.iv_user_set_feed_back);        switch (helper.getItemViewType()) {            case FeedBackEntity.DEFAULT_IMG:                mImageView.setImageResource(item.getDenfultImg());                break;            case FeedBackEntity.SELECT_IMG:                String imgPath = item.getImgPath();                if (TextUtils.isEmpty(imgPath)) {                    mImageView.setImageResource(R.drawable.default_add);                } else {                    Picasso.with(mContext)                            .load(new File(imgPath))                            .fit()                            .config(Bitmap.Config.RGB_565)                            .error(R.drawable.default_add)                            .placeholder(R.drawable.default_add)                            .into(mImageView);                }                break;        }    }}