package com.mzth.createcause.adapter.user;import android.view.View;import com.chad.library.adapter.base.BaseQuickAdapter;import com.chad.library.adapter.base.BaseViewHolder;import com.mzth.createcause.R;import com.mzth.createcause.entity.user.NewsCoreEntity.UserNewsNewsList;import java.util.List;/** * Created by 消息中心适配器 on 2017/10/20 0020. */public class NewsCoreAdapter extends BaseQuickAdapter<UserNewsNewsList, BaseViewHolder> {    public NewsCoreAdapter(List<UserNewsNewsList> data) {        super(R.layout.item_user_news_core, data);    }    @Override    protected void convert(BaseViewHolder helper, UserNewsNewsList item) {        helper.setText(R.id.tv_user_new_new_1, item.getTitle());        helper.setText(R.id.tv_user_new_new_2, item.getAddtime());        helper.setText(R.id.tv_user_new_new_3, item.getContent());        View v_user_new_new_tag = helper.getView(R.id.v_user_new_new_tag);        if (item.getLook().equals("0")) {            v_user_new_new_tag.setVisibility(View.VISIBLE);        } else {            v_user_new_new_tag.setVisibility(View.INVISIBLE);        }        helper.addOnClickListener(R.id.ll_user_new_new_1);    }}