package com.mzth.createcause.adapter.user;import android.graphics.Bitmap;import android.text.TextUtils;import android.widget.ImageView;import com.chad.library.adapter.base.BaseMultiItemQuickAdapter;import com.chad.library.adapter.base.BaseViewHolder;import com.mzth.createcause.R;import com.mzth.createcause.entity.OutPopilEntity_copy;import com.mzth.createcause.util.CommonUtil;import com.squareup.picasso.Picasso;import java.util.List;/** * Created by Administrator on 2017/10/19 0019. */public class OutPupliAdapterContent extends BaseMultiItemQuickAdapter<OutPopilEntity_copy, BaseViewHolder> {    /**     * Same as QuickAdapter#QuickAdapter(Context,int) but with     * some initialization data.     *     * @param data A new list is created out of this one to avoid mutable list     */    public OutPupliAdapterContent(List<OutPopilEntity_copy> data) {        super(data);        addItemType(OutPopilEntity_copy.TITLE, R.layout.item_user_out_pupli_title);        addItemType(OutPopilEntity_copy.CONTENT, R.layout.item_user_out_pupli_content);    }    @Override    protected void convert(BaseViewHolder helper, OutPopilEntity_copy item) {        switch (helper.getItemViewType()) {            case OutPopilEntity_copy.TITLE:                helper.setText(R.id.tv_item_user_out_pupli_title, item.getUserInitial());                break;            case OutPopilEntity_copy.CONTENT:                helper.setText(R.id.tv_item_user_out_pupli_content_1, item.getUserName());                helper.setText(R.id.tv_item_user_out_pupli_content_2, item.getUserPhoto());                ImageView mImageView = helper.getView(R.id.iv_item_user_out_pupli_content);                if (TextUtils.isEmpty(item.getUserImgPath())) {                    mImageView.setImageResource(R.drawable.default_picture);                } else {                    Picasso.with(mContext)                            .load(item.getUserImgPath())                            .resize(CommonUtil.getCommonUtil().convertDIP(45), CommonUtil.getCommonUtil().convertDIP(45))                            .config(Bitmap.Config.RGB_565)                            .error(R.drawable.default_picture)                            .placeholder(R.drawable.default_picture)                            .into(mImageView);                }                break;        }    }}