package com.mzth.createcause.adapter.user;import android.widget.ImageView;import com.chad.library.adapter.base.BaseQuickAdapter;import com.chad.library.adapter.base.BaseViewHolder;import com.mzth.createcause.R;import com.mzth.createcause.entity.PaymentTypeEntity;import java.util.ArrayList;import java.util.List;/** * Created by Administrator on 2017/10/18 0018. */public class PaymentTypeAdapter extends BaseQuickAdapter<PaymentTypeEntity, BaseViewHolder> {    private List<ImageView> imgViews;    public PaymentTypeAdapter(List<PaymentTypeEntity> data) {        super(R.layout.item_user_payment_type, data);        imgViews = new ArrayList<>();    }    @Override    protected void convert(BaseViewHolder helper, PaymentTypeEntity item) {        helper.setImageResource(R.id.iv_item_user_payment_type_1, item.getImgPath());        helper.setText(R.id.tv_item_user_payment_type, item.getPaymentName());        imgViews.add((ImageView) helper.getView(R.id.iv_item_user_payment_type_2));    }    public List<ImageView> getImag() {        return imgViews;    }}