package com.mzth.createcause.adapter.user;import android.text.TextPaint;import android.widget.TextView;import com.chad.library.adapter.base.BaseQuickAdapter;import com.chad.library.adapter.base.BaseViewHolder;import com.mzth.createcause.R;import com.mzth.createcause.entity.user.TaskEntity.TaskList;import java.util.List;/** * Created by Administrator on 2017/10/12 0012. */public class TaskAdapter extends BaseQuickAdapter<TaskList, BaseViewHolder> {    private int listTitle = 0;    public TaskAdapter(List<TaskList> data) {        super(R.layout.item_user_task, data);    }    @Override    protected void convert(BaseViewHolder helper, TaskList item) {        TextView mTextView = helper.getView(R.id.tv_item_user_task_1);        TextPaint paint = mTextView.getPaint();        paint.setFakeBoldText(true);        listTitle++;        mTextView.setText(listTitle + "");        helper.setText(R.id.tv_item_user_task_2,item.getTitle()+" （已完成"+item.getTask_complete_num()+"次)");        if(item.getTask_num().equals(item.getTask_complete_num())){            helper.setText(R.id.tv_item_user_task_3,"已完成");        }else {            helper.setText(R.id.tv_item_user_task_3,"去完成+"+item.getPoints());        }        helper.addOnClickListener(R.id.tv_item_user_task_3);    }}