package com.mzth.createcause.base.custom;import android.content.Context;import android.util.AttributeSet;import android.widget.RelativeLayout;/** * Created by 正方形 on 2017/10/20 0020. */public class SquareLayout extends RelativeLayout{    public SquareLayout(Context context) {        super(context);    }    public SquareLayout(Context context, AttributeSet attrs) {        super(context, attrs);    }    public SquareLayout(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);    }    @Override    protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {        super.onMeasure(widthMeasureSpec, widthMeasureSpec);    }}