package com.mzth.createcause.entity;import org.json.JSONArray;import org.json.JSONObject;import java.util.ArrayList;import java.util.List;/** * Created by 商学院实体类 on 2017/10/20 0020. */public class CommerceEntity {    private String err_msg;    private int status;    private int page;    private int page_total;    private List<CommereceList> mCommereceLists=new ArrayList<>();    public CommerceEntity(JSONObject jsonObject) {        if (jsonObject != null) {            status = jsonObject.optInt("status");            if (status == 1) {                JSONObject mJSONObject = jsonObject.optJSONObject("result");                if (mJSONObject != null) {                    page = mJSONObject.optInt("page");                    page_total = mJSONObject.optInt("page_total");                    JSONArray mJSONArray = mJSONObject.optJSONArray("list");                    if(mJSONArray!=null){                        for (int i = 0; i < mJSONArray.length(); i++) {                            mCommereceLists.add(new CommereceList(mJSONArray.optJSONObject(i)));                        }                    }                }            } else {                err_msg = jsonObject.optString("err_msg");            }        }    }    public String getErr_msg() {        return err_msg;    }    public int getStatus() {        return status;    }    public int getPage() {        return page;    }    public int getPage_total() {        return page_total;    }    public List<CommereceList> getmCommereceLists() {        return mCommereceLists;    }    public class CommereceList {        private  String id;        private  String title;        public CommereceList(JSONObject jsonObject) {            if(jsonObject!=null){                id = jsonObject.optString("id");                title = jsonObject.optString("title");            }        }        public String getId() {            return id;        }        public String getTitle() {            return title;        }    }}