package com.mzth.createcause.entity;import com.chad.library.adapter.base.entity.MultiItemEntity;/** * Created by 意见反馈 on 2017/10/20 0020. */public class FeedBackEntity implements MultiItemEntity {    public static final int DEFAULT_IMG=1;    public static final int SELECT_IMG=2;    private int type;    private int denfultImg;    private String imgPath;    public FeedBackEntity(int type) {        this.type = type;    }    public int getDenfultImg() {        return denfultImg;    }    public void setDenfultImg(int denfultImg) {        this.denfultImg = denfultImg;    }    public String getImgPath() {        return imgPath;    }    public void setImgPath(String imgPath) {        this.imgPath = imgPath;    }    @Override    public int getItemType() {        return type;    }}