package com.mzth.createcause.entity;import com.chad.library.adapter.base.entity.MultiItemEntity;/** * Created by 外门弟子实体类 on 2017/10/19 0019. */public class OutPopilEntity_copy implements MultiItemEntity {    public static final int TITLE=1;    public static final int CONTENT=2;    private String userInitial;    private String userName;    private String userPhoto;    private String userImgPath;    private int type;    public OutPopilEntity_copy(int type) {        this.type = type;    }    public String getUserInitial() {        return userInitial;    }    public void setUserInitial(String userInitial) {        this.userInitial = userInitial;    }    public String getUserName() {        return userName;    }    public void setUserName(String userName) {        this.userName = userName;    }    public String getUserPhoto() {        return userPhoto;    }    public void setUserPhoto(String userPhoto) {        this.userPhoto = userPhoto;    }    public String getUserImgPath() {        return userImgPath;    }    public void setUserImgPath(String userImgPath) {        this.userImgPath = userImgPath;    }    @Override    public int getItemType() {        return type;    }}