package com.mzth.createcause.entity;import com.chad.library.adapter.base.entity.MultiItemEntity;/** * Created by CreateCause on 2017/9/28 0028. */public class SecrchResultEntity implements MultiItemEntity {    public static final int TEXT = 1;    public static final int IMG = 2;    private int itemType;    private String video;    private String img;    public String getVideo() {        return video;    }    public void setVideo(String video) {        this.video = video;    }    public String getImg() {        return img;    }    public void setImg(String img) {        this.img = img;    }    public SecrchResultEntity(int itemType) {        this.itemType = itemType;    }    @Override    public int getItemType() {        return itemType;    }}