package com.mzth.createcause.entity.core;import org.json.JSONArray;import org.json.JSONObject;import java.util.ArrayList;import java.util.List;/** * Created by 资讯实体类 on 2018/1/17 0017. */public class CoreContentEntity {    private int status;    private String err_msg;    private int page;    private int page_total;    private List<InfromationContentList> mInfromationContentLists = new ArrayList<>();    public CoreContentEntity(JSONObject jsonObject) {        if (jsonObject != null) {            status = jsonObject.optInt("status", 0);            if (status == 1) {                JSONObject result = jsonObject.optJSONObject("result");                if (result != null) {                    page = result.optInt("page");                    page_total = result.optInt("page_total");                    JSONArray mJsonArray = result.optJSONArray("list");                    if (mJsonArray != null) {                        for (int i = 0; i < mJsonArray.length(); i++) {                            mInfromationContentLists.add(new InfromationContentList(mJsonArray.optJSONObject(i)));                        }                    } else {                        err_msg = "数据解析异常";                    }                } else {                    err_msg = "数据解析异常";                }            } else {                err_msg = jsonObject.optString("err_msg");            }        }    }    public int getStatus() {        return status;    }    public String getErr_msg() {        return err_msg;    }    public int getPage() {        return page;    }    public int getPage_total() {        return page_total;    }    public List<InfromationContentList> getmInfromationContentLists() {        return mInfromationContentLists;    }    public static class InfromationContentList {        private String id;        private String title;        private String add_time;        private String cat_name;        private String comment;        private String image;        private String vid;        private  String content;        public InfromationContentList(JSONObject jsonObject) {            if (jsonObject != null) {                id = jsonObject.optString("id");                title = jsonObject.optString("title");                add_time = jsonObject.optString("add_time");                cat_name = jsonObject.optString("cat_name");                comment = jsonObject.optString("comment", "");                image = jsonObject.optString("image");                vid = jsonObject.optString("vid");                content = jsonObject.optString("content");            }        }        public String getContent() {            return content;        }        public String getVid() {            return vid;        }        public String getId() {            return id;        }        public String getTitle() {            return title;        }        public String getAdd_time() {            return add_time;        }        public String getCat_name() {            return cat_name;        }        public String getComment() {            return comment;        }        public String getImage() {            return image;        }    }}