package com.mzth.createcause.entity.core;import org.json.JSONArray;import org.json.JSONObject;import java.util.ArrayList;import java.util.List;/** * Created by 资讯分类 on 2018/1/16 0016. */public class CoreTypeEntity {    private int status;    private String err_msg;    private List<InfromactionTypeList> InfromactionTypeLists=new ArrayList<>();    public int getStatus() {        return status;    }    public String getErr_msg() {        return err_msg;    }    public List<InfromactionTypeList> getInfromactionTypeLists() {        return InfromactionTypeLists;    }    public CoreTypeEntity(JSONObject jsonObject) {        if (jsonObject != null) {            status = jsonObject.optInt("status");            if (status == 1) {                JSONArray mJsonArray = jsonObject.optJSONArray("result");                for (int i = 0; i < mJsonArray.length(); i++) {                    InfromactionTypeLists.add(new InfromactionTypeList(mJsonArray.optJSONObject(i)));                }            } else {                err_msg = "数据获取异常，请重试";            }        }    }    public class InfromactionTypeList{        private  String id;        private  String cat_var;        private  String cat_name;        public InfromactionTypeList(JSONObject jsonObject) {            if(jsonObject!=null){                id = jsonObject.optString("id");                cat_var = jsonObject.optString("cat_var");                cat_name = jsonObject.optString("cat_name");            }        }        public String getId() {            return id;        }        public String getCat_var() {            return cat_var;        }        public String getCat_name() {            return cat_name;        }    }}