package com.mzth.createcause.entity.home;import org.json.JSONArray;import org.json.JSONObject;import java.util.ArrayList;import java.util.List;/** * Created by 首页实体类 on 2018/1/12 0012. */public class HomeEntity {    private int status;    private String title;    private int page;    private int page_total;    private String err_msg;    private List<HomeContents> mHomeContentss=new ArrayList<>();    public HomeEntity(JSONObject jsonObject) {        if (jsonObject != null) {            status = jsonObject.optInt("status");            if (status == 1) {                JSONObject result = jsonObject.optJSONObject("result");                if (result != null) {                    title = result.optString("title", "");                    page = result.optInt("page");                    page_total = result.optInt("page_total");                    JSONArray mJsonArray = result.optJSONArray("list");                    if (mJsonArray != null) {                        for (int i = 0; i < mJsonArray.length(); i++) {                            mHomeContentss.add(new HomeContents(mJsonArray.optJSONObject(i)));                        }                    } else {                        err_msg = "数据请求异常,请重试";                    }                } else {                    err_msg = "数据请求异常,请重试";                }            } else {                err_msg = "数据请求异常,请重试";            }        }    }    public int getStatus() {        return status;    }    public String getTitle() {        return title;    }    public int getPage() {        return page;    }    public int getPage_total() {        return page_total;    }    public String getErr_msg() {        return err_msg;    }    public List<HomeContents> getmHomeContentss() {        return mHomeContentss;    }    public static class HomeContents {        private String id;        private String image;        private String name;        private String app_name;        private  String url;        public HomeContents(JSONObject jsonobject) {            if (jsonobject != null) {                id = jsonobject.optString("id");                image = jsonobject.optString("image");                name = jsonobject.optString("name");                app_name = jsonobject.optString("app_name");                url = jsonobject.optString("url");            }        }        public String getUrl() {            return url;        }        public String getId() {            return id;        }        public String getImage() {            return image;        }        public String getName() {            return name;        }        public String getApp_name() {            return app_name;        }    }}