package com.mzth.createcause.entity.login;import org.json.JSONObject;/** * Created by 登录实体类 on 2018/1/16 0016. */public class LoginEntity {    private int status;    private String err_msg;    private  JSONObject result;    private  String id;    private  String username;    private  String email;    private  String phone;    private  String password;    private  String group_var;    private  String nick_name;    private  String type;    private  String parent_uid;    private  String level_id;    private  String record_pass;    private  String points;    private  String openid;    private  String token;    private  String loginTime;    private  String level_name;    private  String auth_key;    private  String avatar;    public LoginEntity(JSONObject jsonObject) {        if (jsonObject != null) {            status = jsonObject.optInt("status");            if(status==1){                result = jsonObject.optJSONObject("result");                if(result!=null){                    id = result.optString("id");                    username = result.optString("username");                    email = result.optString("email");                    phone = result.optString("phone");                    password = result.optString("password");                    group_var = result.optString("group_var");                    nick_name = result.optString("nick_name");                    type = result.optString("type");                    parent_uid = result.optString("parent_uid");                    level_id = result.optString("level_id");                    record_pass = result.optString("record_pass");                    points = result.optString("points");                    openid = result.optString("openid");                    token = result.optString("token");                    loginTime = result.optString("loginTime");                    level_name = result.optString("level_name");                    auth_key = result.optString("auth_key");                    avatar = result.optString("avatar");                }            }else {                err_msg = jsonObject.optString("err_msg");            }        }    }    public String getAvatar() {        return avatar;    }    public String getLevel_name() {        return level_name;    }    public String getAuth_key() {        return auth_key;    }    public int getStatus() {        return status;    }    public String getErr_msg() {        return err_msg;    }    public JSONObject getResult() {        return result;    }    public String getId() {        return id;    }    public String getUsername() {        return username;    }    public String getEmail() {        return email;    }    public String getPhone() {        return phone;    }    public String getPassword() {        return password;    }    public String getGroup_var() {        return group_var;    }    public String getNick_name() {        return nick_name;    }    public String getType() {        return type;    }    public String getParent_uid() {        return parent_uid;    }    public String getLevel_id() {        return level_id;    }    public String getRecord_pass() {        return record_pass;    }    public String getPoints() {        return points;    }    public String getOpenid() {        return openid;    }    public String getToken() {        return token;    }    public String getLoginTime() {        return loginTime;    }}