package com.mzth.createcause.entity.user;import org.json.JSONArray;import org.json.JSONObject;import java.util.ArrayList;import java.util.List;/** * Created by 消息中心实体类 on 2017/10/20 0020. */public class NewsCoreEntity {    private  int status;    private  String err_msg;    private  int page;    private  int page_total;    private List<UserNewsNewsList> mUserNewsNewsLists=new ArrayList<>();    private int no_look;    public NewsCoreEntity(JSONObject jsonObject) {        if(jsonObject!=null){            status = jsonObject.optInt("status", 0);            if(status==1){                JSONObject mjsonObject = jsonObject.optJSONObject("result");                if(mjsonObject!=null){                    page = mjsonObject.optInt("page");                    page_total = mjsonObject.optInt("page_total");                    no_look = mjsonObject.optInt("no_look");                    JSONArray mJSONArray = mjsonObject.optJSONArray("list");                    if(mJSONArray!=null){                        for (int i = 0; i < mJSONArray.length(); i++) {                            mUserNewsNewsLists.add(new UserNewsNewsList(mJSONArray.optJSONObject(i)));                        }                    }                }            }else {                err_msg = jsonObject.optString("err_msg");            }        }    }    public int getNo_look() {        return no_look;    }    public int getStatus() {        return status;    }    public String getErr_msg() {        return err_msg;    }    public int getPage() {        return page;    }    public int getPage_total() {        return page_total;    }    public List<UserNewsNewsList> getmUserNewsNewsLists() {        return mUserNewsNewsLists;    }    public static class UserNewsNewsList{        private  String id;        private  String title;        private  String content;        private  String addtime;        private  String look;        public UserNewsNewsList(JSONObject jsonObject) {            if(jsonObject!=null){                id = jsonObject.optString("id");                title = jsonObject.optString("title");                content = jsonObject.optString("content");                addtime = jsonObject.optString("addtime");                look = jsonObject.optString("look");            }        }        public String getId() {            return id;        }        public String getTitle() {            return title;        }        public String getContent() {            return content;        }        public String getAddtime() {            return addtime;        }        public String getLook() {            return look;        }    }}