package com.mzth.createcause.entity.user;import org.json.JSONArray;import org.json.JSONObject;import java.util.ArrayList;import java.util.List;/** * Created by Administrator on 2017/10/12 0012. */public class TaskEntity {    private int status;    private String err_msg;    private int page;    private int page_total;    private List<TaskList> mTaskLists = new ArrayList<>();    public TaskEntity(JSONObject jsonObject) {        if (jsonObject != null) {            status = jsonObject.optInt("status");            if (status == 1) {                JSONObject mJSONObject = jsonObject.optJSONObject("result");                if (mJSONObject != null) {                    page = mJSONObject.optInt("page", 0);                    page_total = mJSONObject.optInt("page_total", 0);                    JSONArray mJsonArray = mJSONObject.optJSONArray("list");                    if (mJsonArray != null) {                        for (int i = 0; i < mJsonArray.length(); i++) {                            mTaskLists.add(new TaskList(mJsonArray.optJSONObject(i)));                        }                    }                }            } else {                err_msg = jsonObject.optString("err_msg");            }        }    }    public int getStatus() {        return status;    }    public String getErr_msg() {        return err_msg;    }    public int getPage() {        return page;    }    public int getPage_total() {        return page_total;    }    public List<TaskList> getmTaskLists() {        return mTaskLists;    }    public class TaskList {        private String id;        private String title;        private String points;        private String task_type;        private String task_classfy;        private String url_href;        private String task_num;        private String task_complete_num;        private String start_time;        private String end_time;        public TaskList(JSONObject jsonObject) {            if (jsonObject != null) {                id = jsonObject.optString("id");                title = jsonObject.optString("title");                points = jsonObject.optString("points");                task_type = jsonObject.optString("task_type");                task_classfy = jsonObject.optString("task_classfy");                url_href = jsonObject.optString("url_href");                task_num = jsonObject.optString("task_num");                task_complete_num = jsonObject.optString("task_complete_num");                start_time = jsonObject.optString("start_time");                end_time = jsonObject.optString("end_time");            }        }        public String getId() {            return id;        }        public String getTitle() {            return title;        }        public String getPoints() {            return points;        }        public String getTask_type() {            return task_type;        }        public String getTask_classfy() {            return task_classfy;        }        public String getUrl_href() {            return url_href;        }        public String getTask_num() {            return task_num;        }        public String getTask_complete_num() {            return task_complete_num;        }        public String getStart_time() {            return start_time;        }        public String getEnd_time() {            return end_time;        }    }}