package com.mzth.createcause.entity.user;import org.json.JSONObject;/** * Created by 个人信息 on 2018/1/24 0024. */public class UserInfoEntity {    private  int status;    private  String err_msg;    private  String id;    private  String phone;    private  String nick_name;    private  String avatar;    private  String name;    private  String bank_name;    private  String weixin;    private  String zhifubao;    private  String address;    public UserInfoEntity(JSONObject jsonObject) {        if(jsonObject!=null){            status = jsonObject.optInt("status");            if(status==1){                JSONObject result = jsonObject.optJSONObject("result");                if(result!=null){                    id = result.optString("id");                    phone = result.optString("phone");                    nick_name = result.optString("nick_name");                    avatar = result.optString("avatar");                    name = result.optString("name");                    bank_name = result.optString("bank_name");                    weixin = result.optString("weixin");                    zhifubao = result.optString("zhifubao");                    address = result.optString("address");                }            }else {                err_msg = jsonObject.optString("err_msg");            }        }    }    public int getStatus() {        return status;    }    public String getErr_msg() {        return err_msg;    }    public String getId() {        return id;    }    public String getPhone() {        return phone;    }    public String getNick_name() {        return nick_name;    }    public String getAvatar() {        return avatar;    }    public String getName() {        return name;    }    public String getBank_name() {        return bank_name;    }    public String getWeixin() {        return weixin;    }    public String getZhifubao() {        return zhifubao;    }    public String getAddress() {        return address;    }}