package com.mzth.createcause.model.core;import android.text.TextUtils;import android.util.Log;import com.lzy.okgo.OkGo;import com.lzy.okgo.cache.CacheMode;import com.lzy.okgo.callback.StringCallback;import com.mzth.createcause.util.ConfigUtil;/** * Created by 资讯和视频数据 on 2018/1/17 0017. */public class InfromationVideoDataModel implements IInfromationVideoType, IInfromationVideoContent, ICoreInfoData {    private static InfromationVideoDataModel mInfromationVideoDataModel;    private InfromationVideoDataModel() {    }    /**     * 获取分类接口     *     * @return     */    public static IInfromationVideoType getInfromationVideoType() {        if (mInfromationVideoDataModel == null) {            mInfromationVideoDataModel = new InfromationVideoDataModel();        }        return mInfromationVideoDataModel;    }    /**     * 获取内容接口     *     * @return     */    public static IInfromationVideoContent getInfromationVideoContent() {        if (mInfromationVideoDataModel == null) {            mInfromationVideoDataModel = new InfromationVideoDataModel();        }        return mInfromationVideoDataModel;    }    /**     * 获取详情数据接口     *     * @return     */    public static ICoreInfoData getICoreInfoData() {        if (mInfromationVideoDataModel == null) {            mInfromationVideoDataModel = new InfromationVideoDataModel();        }        return mInfromationVideoDataModel;    }    @Override    public void queryType(StringCallback callback) {        if (callback != null) {            OkGo.<String>post(ConfigUtil.CORE_TYPE)                    .execute(callback);        }    }    @Override    public void queryContent(String typeID, String type, String page, StringCallback callback) {        if (!TextUtils.isEmpty(typeID) && !TextUtils.isEmpty(type)) {            if (callback != null) {                OkGo.<String>post(ConfigUtil.CORE_DATA)                        .params("cat_id", typeID)                        .params("type", type)                        .params("page", page)                        .cacheMode(CacheMode.FIRST_CACHE_THEN_REQUEST)                        .execute(callback);            }        }    }    @Override    public void queryCoreInfoData(String coreId, StringCallback callBack) {        if (!TextUtils.isEmpty(coreId)) {            OkGo.<String>post(ConfigUtil.CORE_INFO_DATA)                    .params("id", coreId)                    .execute(callBack);        }    }    @Override    public void queryCoreInfoCommentData(String id,String page,String userID,StringCallback callBack) {        if (!TextUtils.isEmpty(id)) {            OkGo.<String>post(ConfigUtil.CORE_COMMENT_DATA)                    .params("id", id)                    .params("page", page)                    .params("user_id", userID)                    .execute(callBack);        }    }}