package com.mzth.createcause.model.user;import android.text.TextUtils;import com.lzy.okgo.OkGo;import com.lzy.okgo.callback.StringCallback;import com.mzth.createcause.entity.user.NewsCoreEntity;import com.mzth.createcause.util.ConfigUtil;/** * Created by 我的消息中心 on 2018/1/23 0023. */public class UserNewsModel implements IUserNewsNews {    private static UserNewsModel mUserNewsModel;    private UserNewsModel() {    }    /**     * 获取通知接口     *     * @return     */    public static IUserNewsNews getUserNewsNews() {        if (mUserNewsModel == null) {            mUserNewsModel = new UserNewsModel();        }        return mUserNewsModel;    }    @Override    public void queryUserNewsData(String userId, String auth_key,String page, StringCallback callBack) {        if (!TextUtils.isEmpty(userId) && !TextUtils.isEmpty(auth_key)) {            OkGo.<String>post(ConfigUtil.USER_NEWS_NEWS)                    .params("user_id", userId)                    .params("auth_key", auth_key)                    .params("page", page)                    .execute(callBack);        }    }}