package com.mzth.createcause.model.user.impl;import com.mzth.createcause.R;import com.mzth.createcause.entity.PaymentTypeEntity;import com.mzth.createcause.model.user.IPaymentType;import java.util.ArrayList;import java.util.List;/** * Created by 购买股份支付方式 on 2017/10/18 0018. */public class PaymentTypeModel implements IPaymentType {    private static IPaymentType mIPaymentType;    private PaymentTypeModel() {    }    public static IPaymentType getIPaymentType() {        if (mIPaymentType == null) {            mIPaymentType = new PaymentTypeModel();        }        return mIPaymentType;    }    @Override    public List<PaymentTypeEntity> getPaymentType() {        List<PaymentTypeEntity> mPaymentTypeEntitys = new ArrayList<>();        int[] mImgs = new int[]{                R.drawable.pay,                R.drawable.wechat        };        String[] mPayName = new String[]{                "支付宝",                "微信"        };        for (int i = 0; i < mImgs.length; i++) {            PaymentTypeEntity mPaymentTypeEntity = new PaymentTypeEntity();            mPaymentTypeEntity.setImgPath(mImgs[i]);            mPaymentTypeEntity.setPaymentName(mPayName[i]);            mPaymentTypeEntitys.add(mPaymentTypeEntity);        }        return mPaymentTypeEntitys;    }}