package com.mzth.createcause.presenter.user;import android.os.Handler;import android.os.Message;import android.view.View;import com.mzth.createcause.model.user.IOutPuoIl;import com.mzth.createcause.model.user.impl.OutPopilModel;import com.mzth.createcause.view.fragment.user.outpupil.IOutPupilActivity;import java.util.Timer;import java.util.TimerTask;/** * Created by Administrator on 2017/10/19 0019. */public class OutPopilPressenter {    private static OutPopilPressenter mOutPopilPressenter;    private IOutPupilActivity iOutPupilActivity;    /**     * 初始化UI     *     * @param iOutPupilActivity     */    public void initIOutPupilAdapter(IOutPupilActivity iOutPupilActivity) {        this.iOutPupilActivity = iOutPupilActivity;    }    private OutPopilPressenter() {    }    public static OutPopilPressenter getOutPopilPressenter() {        if (mOutPopilPressenter == null) {            mOutPopilPressenter = new OutPopilPressenter();        }        return mOutPopilPressenter;    }    /**     * 初始化数据     */    public void initContentData() {        if (iOutPupilActivity != null) {            IOutPuoIl mIOutPuoIl = OutPopilModel.getIOutPuoIl();            iOutPupilActivity.initAdapter(mIOutPuoIl.getPuoilList());        }    }    /**     * 模糊查询     *     * @param search     */    public void findContent(String search) {        IOutPuoIl mIOutPuoIl = OutPopilModel.getIOutPuoIl();        iOutPupilActivity.initAdapter(mIOutPuoIl.findPuoilList(search));    }    /**     * 设置提示框的状态     */    public void initHintVisStaius() {        iOutPupilActivity.setVisibilityStatus(View.VISIBLE);        new Timer().schedule(new TimerTask() {            @Override            public void run() {                mHandler.sendEmptyMessage(0);            }        }, 1000);    }    private Handler mHandler = new Handler() {        @Override        public void handleMessage(Message msg) {            super.handleMessage(msg);            switch (msg.what) {                case 0:                    iOutPupilActivity.setVisibilityStatus(View.INVISIBLE);                    break;            }        }    };}