package com.mzth.createcause.presenter.user;import com.mzth.createcause.entity.PaymentTypeEntity;import com.mzth.createcause.model.user.impl.PaymentTypeModel;import com.mzth.createcause.view.fragment.user.money.IPaymentTypeActivity;import java.util.List;/** * Created by 股份购买支付方式控制器 on 2017/10/18 0018. */public class PaymentTypePressenter {    private IPaymentTypeActivity iPaymentTypeActivity;    private static PaymentTypePressenter mPaymentTypePressenter;    private PaymentTypePressenter() {    }    public static PaymentTypePressenter getPaymentTypePressenter() {        if (mPaymentTypePressenter == null) {            mPaymentTypePressenter = new PaymentTypePressenter();        }        return mPaymentTypePressenter;    }    /**     * 初始化UI     */    public void initIPaymentTypeActivity(IPaymentTypeActivity iPaymentTypeActivity) {        this.iPaymentTypeActivity = iPaymentTypeActivity;    }    /**     * 获取数据     */    public void getData() {        List<PaymentTypeEntity> paymentType = PaymentTypeModel.getIPaymentType().getPaymentType();        if (iPaymentTypeActivity != null) {            iPaymentTypeActivity.initAdapter(paymentType);        }    }}