package com.mzth.createcause.view.fragment.user.money;import android.graphics.Color;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.View;import android.widget.ImageView;import android.widget.TextView;import com.mzth.createcause.R;import com.mzth.createcause.adapter.user.WithdrawDepositInfoAdapter;import com.mzth.createcause.base.BaseActivity;import com.mzth.createcause.entity.WithdrawDepositInfoEntity;import com.mzth.createcause.util.CommonUtil;import com.mzth.createcause.util.DividerItemDecoration;import java.util.ArrayList;import java.util.List;/** * 提现记录 */public class WithdrawDepositInfoActivity extends BaseActivity {    private RecyclerView rv_withdraw_desposit_info;    private TextView base_title;    private ImageView base_iv;    private TextView base_tv;    @Override    protected void setActivity() {        setContentView(R.layout.activity_withdraw_deposit_info);    }    @Override    protected void initView() {        rv_withdraw_desposit_info= CommonUtil.getCommonUtil().bindView(this,R.id.rv_withdraw_desposit_info);        base_title= CommonUtil.getCommonUtil().bindView(this,R.id.base_title);        base_iv= CommonUtil.getCommonUtil().bindView(this,R.id.base_iv);        base_tv= CommonUtil.getCommonUtil().bindView(this,R.id.base_tv);    }    @Override    protected void initSet() {        base_title.setText("提现记录");        base_iv.setVisibility(View.GONE);        base_tv.setVisibility(View.GONE);    }    @Override    protected void initAdapter() {    }    @Override    protected void initData() {        List<WithdrawDepositInfoEntity> mWithdrawDepositInfoEntitys = new ArrayList<>();        for (int i = 0; i < 10; i++) {            mWithdrawDepositInfoEntitys.add(new WithdrawDepositInfoEntity());        }        WithdrawDepositInfoAdapter mWithdrawDepositInfoAdapter = new WithdrawDepositInfoAdapter(mWithdrawDepositInfoEntitys);        rv_withdraw_desposit_info.setLayoutManager(new LinearLayoutManager(mContext,LinearLayoutManager.VERTICAL,false));        rv_withdraw_desposit_info.addItemDecoration(new DividerItemDecoration(mContext,                LinearLayoutManager.HORIZONTAL,                CommonUtil.getCommonUtil().convertDIP(8),                Color.parseColor("#f8f8f8")));        rv_withdraw_desposit_info.setAdapter(mWithdrawDepositInfoAdapter);    }    @Override    protected void initListener() {    }}