package com.mzth.createcause.view.fragment.user.money.impl;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.View;import android.widget.ImageView;import android.widget.TextView;import com.chad.library.adapter.base.BaseQuickAdapter;import com.mzth.createcause.R;import com.mzth.createcause.adapter.user.PaymentTypeAdapter;import com.mzth.createcause.base.BaseActivity;import com.mzth.createcause.entity.PaymentTypeEntity;import com.mzth.createcause.presenter.user.PaymentTypePressenter;import com.mzth.createcause.util.CommonUtil;import com.mzth.createcause.util.DividerItemDecoration;import com.mzth.createcause.view.fragment.user.money.IPaymentTypeActivity;import java.util.List;public class PayMentTypeActivity extends BaseActivity implements IPaymentTypeActivity {    private TextView base_title;    private ImageView base_iv;    private TextView base_tv;    private RecyclerView rv_user_payment_type;    private PaymentTypePressenter mPaymentTypePressenter;    @Override    protected void setActivity() {        setContentView(R.layout.activity_pay_ment_type);        mPaymentTypePressenter = PaymentTypePressenter.getPaymentTypePressenter();        mPaymentTypePressenter.initIPaymentTypeActivity(this);    }    @Override    protected void initView() {        base_title = CommonUtil.getCommonUtil().bindView(this, R.id.base_title);        base_iv = CommonUtil.getCommonUtil().bindView(this, R.id.base_iv);        base_tv = CommonUtil.getCommonUtil().bindView(this, R.id.base_tv);        rv_user_payment_type = CommonUtil.getCommonUtil().bindView(this, R.id.rv_user_payment_type);    }    @Override    protected void initSet() {        base_title.setText("支付方式");        base_iv.setVisibility(View.GONE);        base_tv.setVisibility(View.GONE);    }    @Override    protected void initAdapter() {    }    @Override    protected void initData() {        mPaymentTypePressenter.getData();    }    @Override    protected void initListener() {    }    @Override    public void initAdapter(List<PaymentTypeEntity> paymentTypeEntities) {        PaymentTypeAdapter mPaymentTypeAdapter = new PaymentTypeAdapter(paymentTypeEntities);        rv_user_payment_type.setLayoutManager(new LinearLayoutManager(mContext, LinearLayoutManager.VERTICAL, false));        rv_user_payment_type.addItemDecoration(new DividerItemDecoration(mContext, LinearLayoutManager.HORIZONTAL));        rv_user_payment_type.setAdapter(mPaymentTypeAdapter);        final List<ImageView> imag = mPaymentTypeAdapter.getImag();        mPaymentTypeAdapter.setOnItemClickListener(new BaseQuickAdapter.OnItemClickListener() {            @Override            public void onItemClick(BaseQuickAdapter adapter, View view, int position) {                for (int i = 0; i < imag.size(); i++) {                    if (i == position) {                        imag.get(i).setImageResource(R.drawable.select_icon);                    } else {                        imag.get(i).setImageResource(R.color.colorWhite);                    }                }            }        });    }}